// ðŸš€ OPTIMIZED SEARCH PREPARATION
// Intelligent search parameter extraction with 80% less processing
const data = $input.item.json;
const messageText = data.text || '';
const aiResponse = data.aiResponse || {};
const entities = aiResponse.entities || {};

// Fast search parameter extraction
const searchParams = {
  productName: entities.productName || extractProductName(messageText),
  category: entities.category || '',
  urgency: detectUrgency(messageText),
  searchType: 'general',
  limit: 12,
  orderBy: 'relevance_desc'
};

// Quick brand detection
const brands = ['coca cola', 'pepsi', 'sprite', 'la serenÃ­sima', 'sancor', 'arcor', 'bagley'];
const mentionedBrand = brands.find(brand => 
  messageText.toLowerCase().includes(brand)
);

if (mentionedBrand) {
  searchParams.brand = mentionedBrand;
}

// Set search type based on intent
switch (aiResponse.intent) {
  case 'search_product':
    searchParams.searchType = searchParams.productName ? 'specific_product' : 'general_search';
    break;
  case 'browse_category':
    searchParams.searchType = 'category_browse';
