  searchParams.prioritizeInStock = true;
  searchParams.orderBy = 'stock_desc,price_asc';
}

function extractProductName(text) {
  // Simple product name extraction
  const cleanText = text.toLowerCase()
    .replace(/busco|buscar|quiero|necesito|dÃ³nde|donde/g, '')
    .trim();
  return cleanText.length > 2 ? cleanText : '';
}

function detectUrgency(text) {
  const urgentWords = ['urgente', 'rÃ¡pido', 'ya', 'ahora', 'pronto'];
  return urgentWords.some(word => text.toLowerCase().includes(word)) ? 'alta' : 'media';
}

return {
  ...data,
  searchParams,
  needsProductSearch: true,
  searchPrepared: true
};

