    skipProcessing: true,
    reason: 'unsupported_type' 
  };
}

const parsed = {
  messageId: message.id,
  timestamp: parseInt(message.timestamp),
  customerPhone: message.from,
  customerName: contact?.profile?.name || 'Cliente',
  messageType: message.type,
  text: '',
  valid: false
};

// Parseo simple
if (message.type === 'text') {
  parsed.text = message.text?.body || '';
  parsed.valid = parsed.text.length > 0;
} else if (message.type === 'interactive') {
  if (message.interactive?.type === 'list_reply') {
    parsed.text = message.interactive.list_reply.id;
    parsed.interactionType = 'list';
  } else if (message.interactive?.type === 'button_reply') {
    parsed.text = message.interactive.button_reply.id;
    parsed.interactionType = 'button';
  }
  parsed.valid = true;
}

return parsed;

