// FORMAT WHATSAPP RESPONSE MVP
const data = $json;
const whatsappResponse = data.whatsappResponse;
const customerPhone = data.customerPhone;

if (!whatsappResponse || !customerPhone) {
  return { ...data, skipWhatsAppSend: true };
}

const cleanPhone = customerPhone.replace(/[^\d]/g, '');

let whatsappPayload = {
  messaging_product: 'whatsapp',
  to: cleanPhone,
  type: whatsappResponse.messageType || 'text'
};

switch (whatsappResponse.messageType) {
  case 'text':
    whatsappPayload.text = { 
      body: whatsappResponse.text.substring(0, 4096) 
    };

    // Agregar quick replies si existen
    if (whatsappResponse.quickReplies && whatsappResponse.quickReplies.length > 0) {
      whatsappPayload.type = 'interactive';
      whatsappPayload.interactive = {
        type: 'button',
        body: { text: whatsappResponse.text },
        action: {
          buttons: whatsappResponse.quickReplies.slice(0, 3).map(reply => ({
            type: 'reply',
            reply: { id: reply.id, title: reply.title }
          }))
        }
      };
    }
    break;

  case 'interactive':
    whatsappPayload.interactive = {
      type: whatsappResponse.interactiveType || 'button',
      body: { text: whatsappResponse.text }
    };

    if (whatsappResponse.interactiveType === 'list') {
      whatsappPayload.interactive.action = {
        button: whatsappResponse.buttonText || 'Opciones',
        sections: whatsappResponse.listSections || []
      };
    } else {
      whatsappPayload.interactive.action = {
        buttons: whatsappResponse.buttons || []
      };
    }

    if (whatsappResponse.footerText) {
      whatsappPayload.interactive.footer = { 
        text: whatsappResponse.footerText 
      };
    }
    break;
}

return {
  ...data,
  whatsappPayloadToSend: whatsappPayload,
  readyToSend: true
};
